import React, { ReactElement } from "react"
import { NavLink } from "react-router-dom"
import { ladderIcon, mapIcon, searchIcon, skateLogoIcon } from "../helpers/icon"
import featureIsEnabled from "../laboratoryFeatures"

interface Props {
  pickerContainerIsVisible: boolean
}

const TabBar = ({
  pickerContainerIsVisible,
}: Props): ReactElement<HTMLDivElement> => (
  <div
    className={`m-tab-bar ${pickerContainerIsVisible ? "visible" : "hidden"}`}
  >
    <button
      className="m-tab-bar__logo"
      onClick={() => window.location.reload()}
    >
      {skateLogoIcon("m-tab-bar__icon")}
    </button>
    <ul className="m-tab-bar__links">
      <li>
        <NavLink
          activeClassName="m-tab-bar__link--active"
          className="m-tab-bar__link"
          exact={true}
          title="Routes"
          to="/"
        >
          {ladderIcon("m-tab-bar__icon")}
        </NavLink>
      </li>
      <li>
        <NavLink
          activeClassName="m-tab-bar__link--active"
          className="m-tab-bar__link"
          exact={true}
          title="Shuttle Map"
          to="/shuttle-map"
        >
          {mapIcon("m-tab-bar__icon")}
        </NavLink>
      </li>
      <li>
        <NavLink
          activeClassName="m-tab-bar__link--active"
          className="m-tab-bar__link"
          title="Settings"
          to="/settings"
        >
          {settingsIcon}
        </NavLink>
      </li>
      <li>
        <NavLink
          activeClassName="m-tab-bar__link--active"
          className="m-tab-bar__link"
          title="About Skate"
          to="/about"
        >
          {aboutIcon}
        </NavLink>
      </li>
      {featureIsEnabled("search_page") ? (
        <li>
          <NavLink
            activeClassName="m-tab-bar__link--active"
            className="m-tab-bar__link"
            title="Search"
            to="/search"
          >
            {searchIcon("m-tab-bar__icon")}
          </NavLink>
        </li>
      ) : null}
    </ul>
    <div className="m-tab-bar__t-logo">{tLogo}</div>
  </div>
)

